    let arText = ['khng', 'mt', 'hai', 'ba', 'bn', 'nm', 'su', 'by', 'tm', 'chn'];

    function readDozens(number, fully) {
        let string = "",
            dozen = Math.floor(number / 10),
            unit = number % 10;
        if (dozen > 1) {
            string = " " + arText[dozen] + " mi";
            if (unit === 1) {
                string += " mt";
            }
        } else if (dozen === 1) {
            string = " mi";
            if (unit === 1) {
                string += " mt";
            }
        } else if (fully && unit > 0) {
            string = " l";
        }
        if (unit === 5 && dozen >= 1) {
            string += " lm";
        } else if (unit > 1 || (unit === 1 && dozen === 0)) {
            string += " " + arText[unit];
        }
        return string;
    }
    function readBlock(number, fully) {
        let string = "",
            hundred = Math.floor(number / 100);
        number = number % 100;
        if (fully || hundred > 0) {
            string = " " + arText[hundred] + " trm";
            string += readDozens(number, true);
        } else {
            string = readDozens(number, false);
        }
        return string;
    }
    function readMillions(number, fully) {
        let string = "",
            million = Math.floor(number / 1000000);
        number = number % 1000000;
        if (million > 0) {
            string = readBlock(million, fully) + " triu";
            fully = true;
        }
        let thousand = Math.floor(number / 1000);
        number = number % 1000;
        if (thousand > 0) {
            string += readBlock(thousand, fully) + " nghn";
            fully = true;
        }
        if (number > 0) {
            string += readBlock(number, fully);
        }
        return string;
    }
    function numberToText(number) {
        if (number === 0) return ' ' + arText[0];
        let string = "", suffixes = "";
        do {
            let Billion = number % 1000000000;
            number = Math.floor(number / 1000000000);
            if (number > 0) {
                string = readMillions(Billion, true) + suffixes + string;
            } else {
                string = readMillions(Billion, false) + suffixes + string;
            }
            suffixes = " t";
        } while (number > 0);
        return string;
    }

